import './App.css';
import { SetStateAction, useState } from 'react';
import { Alert, Box, Button, Snackbar, TextField, Typography } from '@mui/material';
import axios from 'axios';

function App() {
  const [inputText, setInputText] = useState<string>('');
  const [inputRes, setInputRes] = useState<string>('');
  const [resSeverity, setResSeverity] = useState<string>('');

  const [openSnackBar, setOpenSnackBar] = useState<boolean>(false);

  const handleSubmit = (e: { preventDefault: () => void }) => {
    e.preventDefault();

    // Send a POST request
    axios
      .post('http://localhost:8081/input', { inputText })
      .then(res => {
        typeof res === 'string' && setInputRes(res);
      })
      .catch(err => console.log(err));

    setInputText('');
  };

  const handleCloseSnackBar = (event?: React.SyntheticEvent | Event, reason?: string) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpenSnackBar(false);
  };

  const handleInput = (e: { target: { value: SetStateAction<string> } }) => {
    setInputText(e.target.value);
  };

  return (
    <Box sx={{ background: '#051942', height: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white' }}>
      <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'center' }}>
        <Typography> Enter your query below</Typography>
        <TextField
          id="outlined-basic"
          multiline
          value={inputText}
          onChange={handleInput}
          label="Outlined"
          variant="outlined"
          maxRows={4}
          InputProps={{ sx: { color: 'white', border: '1px solid white', '& #outlined-basic': { borderColor: 'white' } } }}
          InputLabelProps={{ sx: { display: 'none' } }}
          sx={{ width: '30rem' }}
        />
        <Button variant="outlined" type="submit" sx={{ alignSelf: 'flex-end', mt: 2 }}>
          Submit
        </Button>
      </Box>

      <Snackbar open={openSnackBar} autoHideDuration={6000} onClose={handleCloseSnackBar}>
        <Alert onClose={handleCloseSnackBar} severity={resSeverity} sx={{ width: '100%' }}>
          {inputRes}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default App;
